    private void header_mainMousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_header_mainMousePressed
       xMouse = evt.getX();
       yMouse = evt.getY();
    }//GEN-LAST:event_header_mainMousePressed

    private void header_mainMouseDragged(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_header_mainMouseDragged
       int x = evt.getXOnScreen();
       int y = evt.getYOnScreen();
        this.setLocation((x-xMouse),(y-yMouse));
    }//GEN-LAST:event_header_mainMouseDragged

