    private void usuario_fieldMousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_usuario_fieldMousePressed
        if (usuario_field.getText().equals("example@xubax.com")) {
            usuario_field.setText("");
            usuario_field.setForeground(Color.black);
        }
        if (String.valueOf(password_Field.getPassword()).isEmpty()) {
            password_Field.setText("********");
            password_Field.setForeground(Color.gray);
        }
        

        
    }//GEN-LAST:event_usuario_fieldMousePressed

    private void password_FieldMousePressed(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_password_FieldMousePressed
        if (usuario_field.getText().isEmpty()) {
            usuario_field.setText("example@xubax.com");
            usuario_field.setForeground(Color.gray); 
        }
        
         if (String.valueOf(password_Field.getPassword()).equals("********")) {
        password_Field.setText("");
        password_Field.setForeground(Color.black);
         }
    }//GEN-LAST:event_password_FieldMousePressed

    private void boton_entrarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_boton_entrarMouseClicked
        System.out.println("usuario: " + usuario_field.getText() + "\n contraseña: " + String.valueOf(password_Field.getPassword()));
    }//GEN-LAST:event_boton_entrarMouseClicked

